class Solution:
  def characterReplacement(self, s: str, k: int) -> int:
    left: int = 0
    maxLen: int = 0
    maxChar: int = 0
    freqency: dict[str, int] = {}
    for right in range(len(s)):
      freqency[s[right]] = freqency.get(s[right], 0) + 1
      maxChar = max(maxChar, freqency[s[right]]) # maxChar in current window
      length = right - left + 1
      if length > k + maxChar: # if adding a character to the window is greater than the maxFreq + k value then we change the size of the window 
        freqency[s[left]] -= 1
        left +=1
        length -= 1

      maxLen =  max(maxLen, length)
    return maxLen

if __name__ == '__main__':
  s = Solution()
  print(s.characterReplacement('ABAB', 2))
  print(s.characterReplacement('AABABBA', 1))
  print(s.characterReplacement('AABABBBA', 2))
  print(s.characterReplacement('AABBB', 2))
  print(s.characterReplacement('', 7520))